package joshua.ejercicios;

import java.util.ArrayList;

public class Principal {
	
	public long problema8(){
		String data = "";
		long retorno = 0;
		ArrayList<Long> productos = new ArrayList<Long>();
		ArrayList<String> items = new ArrayList<String>();
		
		for (int i=0, j=13; i<data.length()-13; i ++, j++){
			String _4Seguidos = data.substring(i, j);
			long prod = 1;
			for(int k=0; k<_4Seguidos.length();k++){
				prod *= Long.parseLong( _4Seguidos.substring(k, k+1) );
			}
			productos.add(prod);
			items.add(_4Seguidos);
		}
		int h=0;
		for(long numero : productos){
			if (esMayor(numero, productos)){
				retorno = numero;
				System.out.println("Item:" +items.get(h));
				break;
			}			
			h++;
		}
				
		return retorno;
	}
	
	private boolean esMayor(long numero, ArrayList<Long> ly_datos){
		for(long num : ly_datos){
			if (num > numero){
				return false;
			}
		}
		return true;
	}
	
	public static void main(String[] args){
		System.out.println("Problema 8");
		Principal p = new Principal();
		long resultado = p.problema8();
		System.out.println("numero: "+resultado);
	}

}
